<?phpclass DataBase{    private $host = "localhost";    private $db_name = "api_db";    private $username = "root";    private $password = "";    public $connection;    public function __construct()    {    }    public function getConnection()    {        $this->connection = null;        try{            $this->connection = new PDO("mysql:host=".$this->host.";dbname=".$this->db_name,$this->username,$this->password);            $this->connection->exec("set names utf8");        }        catch (Exception $exeption){            echo "Connection error : ".$exeption->getMessage();        }        return $this->connection;    }}