<?phpclass Product{    private $connection;    private $table;    public $id;    public $name;    public $description;    public $price;    public $category_id;    public $category_name;    public $created;    public function __construct($db)    {        $this->connection = $db;    }    public function read()    {        $query = "SELECT c.name as category_name, p.id, p.name, p.description, p.price, p.category_id, p.created         FROM ".$this->table. "p         LEFT JOIN categories c         ON p.category_id = c.id         ORDER BY p.created DESC";        $stmt = $this->connection->prepare($query);        $stmt->execute();        return $stmt;    }    public function create()    {        $query = "INSERT INTO".$this->table.            "SET name=:name, price=:price, description=:description, category_id=:category_id, created=:created";        $stmt = $this->connection->prepare($query);        //clear product tag        $this->name = htmlspecialchars(strip_tags($this->name));        $this->price = htmlspecialchars(strip_tags($this->price));        $this->description = htmlspecialchars(strip_tags($this->description));        $this->category_id = htmlspecialchars(strip_tags($this->category_id));        $this->category_name = htmlspecialchars(strip_tags($this->category_name));        $stmt->bindParam(":name", $this->name);        $stmt->bindParam(":price", $this->price);        $stmt->bindParam(":description", $this->description);        $stmt->bindParam(":category_id", $this->category_id);        $stmt->bindParam(":created", $this->created);        if ($stmt->execute()) return true;        return false;    }}